con:config.script:
def loadTestContext = testRunner.runContext.getProperty('LoadTestContext');
def studyArr = loadTestContext.getProperty('studyIds');
def orgsArr = loadTestContext.getProperty('siteIds');
//Initialize
int rpIndex = Integer.parseInt(testRunner.testCase.testSteps["Properties"].getPropertyValue("rpIndex"));
int participantIndex = Integer.parseInt(testRunner.testCase.testSteps["Properties"].getPropertyValue("participantIndex"));
int orgIndex = Integer.parseInt(testRunner.testCase.testSteps["Properties"].getPropertyValue("orgIndex"));

rpIndex ++;

// recycle 
if ( rpIndex == 11 ) { // completed all 10 TAC 
	participantIndex = (participantIndex + 1) ; // Move to next participant
	testRunner.testCase.testSteps["Properties"].setPropertyValue("participantIndex",String.valueOf(participantIndex));
	rpIndex = 1;
} 

testRunner.testCase.testSteps["Properties"].setPropertyValue("rpIndex",String.valueOf(rpIndex) );

if ( participantIndex == 5 ) { // Completed all 5 participants, Move to next organization.
	orgIndex = ( orgIndex  + 1 );
	testRunner.testCase.testSteps["Properties"].setPropertyValue("orgIndex",String.valueOf(orgIndex));
	participantIndex = 0;
	testRunner.testCase.testSteps["Properties"].setPropertyValue("participantIndex",String.valueOf(participantIndex));
}

String offsetStudy =  testRunner.testCase.testSteps["Properties"].getPropertyValue("offsetStudy");
int offsetStudyVal = Integer.parseInt(offsetStudy);

if (orgIndex == orgsArr.size  )  { // Completed populating for 1 study.
	offsetStudyVal++;
	testRunner.testCase.testSteps["Properties"].setPropertyValue("offsetStudy",String.valueOf(offsetStudyVal) );
	orgIndex=0;
	testRunner.testCase.testSteps["Properties"].setPropertyValue("orgIndex",String.valueOf(orgIndex));
}



log.info('run='+context.RunCount + ' rpIndex='+ rpIndex+ ' participantIndex='+ participantIndex + ' orgIndex='+ orgIndex + ' offsetStudyVal='+offsetStudyVal);

String study = studyArr[offsetStudyVal].replaceAll("[,.\\-\\s+\\n\\t+]", "");
String StudySubjectId = study + "." +  orgsArr[orgIndex] + "." + participantIndex ;
testRunner.testCase.testSteps["Properties"].setPropertyValue("studyId",studyArr[offsetStudyVal]);
testRunner.testCase.testSteps["Properties"].setPropertyValue("StudySubjectId",StudySubjectId);

log.info('run='+context.RunCount + ' rpIndex='+ rpIndex+ ' participantIndex='+ participantIndex + ' orgIndex='+ orgIndex + ' offsetStudyVal='+offsetStudyVal);
